INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1439, '52.72.2', 'Ремонт прочих бытовых электрических изделий', 'Ремонт прочих бытовых электрических изделий \nЭта группировка включает: \n- ремонт  бытовых  электрических изделий (холодильников, морозильников,  стиральных и сушильных машин,  кухонного оборудования,    нагревательных   приборов,   пылесосов, швейных машин и других мелких бытовых приборов)', 1437, '52.72', 0);
