INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1441, '52.74', 'Ремонт бытовых изделий и предметов личного пользования, не включенных в другие группировки', 'Ремонт бытовых изделий и предметов личного  пользования, не включенных в другие группировки \nЭта группировка включает: \n- ремонт  одежды   и    текстильных   изделий   бытового назначения \n- настройку пианино и других музыкальных инструментов \n- ремонт спортивного и туристского оборудования \n- ремонт мобильных телефонов \n- ремонт фотоаппаратуры \n- оказание  услуг   в  присутствии  заказчика,   включая нанесение рисунков на текстильные изделия \n- ремонт прочих  бытовых  изделий  и  предметов  личного пользования, не включенных в другие группировки \nЭта группировка не включает: \n- ремонт и реставрацию мебели, см. 36.1 \n- техническое   обслуживание   и    ремонт   музыкальных инструментов, см. 36.30 \n- техническое обслуживание и ремонт домовых отопительных котлов и бойлеров, см. 45.33 \n- ремонт  и   реставрацию  произведений  искусства,  см. 92.31', 1435, '52.7', 0);
