INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1446, '55.10', 'Деятельность гостиниц', 'Деятельность гостиниц \nЭта группировка включает: \n- предоставление мест для временного проживания: \n- в отелях, мотелях и загородных гостиницах \n- в отелях, с условиями для проведения конференций \nЭта группировка не включает: \n- сдачу   внаем   жилых   зданий   и   помещений    для долгосрочного проживания, см. 70.20', 1445, '55.1', 0);
