INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1454, '55.23.4', 'Предоставление мест для временного проживания в железнодорожных спальных вагонах и прочих транспортных средствах', 'Предоставление мест   для   временного   проживания    в железнодорожных  спальных  вагонах и прочих транспортных средствах', 1450, '55.23', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1455, '55.23.5', 'Деятельность прочих мест для временного проживания, не включенных в другие группировки', 'Деятельность прочих мест для временного  проживания,  не включенных в другие группировки \nЭта группировка включает: \n- предоставление мест для временного проживания: \n- на квартирах, в сельских домах, домах для  приезжих  и т.п. \n- в общежитиях для студентов, школах-интернатах \n- в общежитиях для сезонных рабочих и т.п. \nЭта группировка не включает: \n- сдачу внаем жилых зданий и помещений для долгосрочного проживания, см. 70.20.1', 1450, '55.23', 0);
