INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1457, '55.30', 'Деятельность ресторанов и кафе', 'Деятельность ресторанов и кафе \nЭта группировка включает: \n- изготовление,  реализацию  и  организацию  потребления непосредственно  на месте кулинарной продукции,  продажу напитков,  иногда   в   сопровождении   некоторых   форм развлекательных программ: \n- ресторанами   и    кафе    с    полным    ресторанным обслуживанием \n- предприятиями      общественного       питания      с самообслуживанием \n- деятельность   закусочных    (предприятий    "быстрого питания") \n- обеспечение      питанием      в       железнодорожных вагонах \n- ресторанах и на судах \n- реализацию кулинарной продукции ресторанами и кафе вне предприятия \nЭта группировка не включает: \n- торговлю через автоматы, см. 52.63', 1456, '55.3', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1458, '55.4', 'Деятельность баров', 'Деятельность баров', 1444, '55', 1);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1459, '55.40', 'Деятельность баров', 'Деятельность баров \nЭта группировка включает: \n- продажу напитков,  изготовление  и  продажу   большого ассортимента коктейлей, а также закусок и  десертов  для потребления на месте, иногда в  сопровождении  некоторых форм развлекательных  программ:  барах,  ночных  клубах, пивных барах и т.п. \nЭта группировка не включает: \n- торговлю через автоматы, см. 52.63 \n- продажу напитков для потребления не на месте  продажи, см. 52', 1458, '55.4', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1460, '55.5', 'Деятельность столовых при предприятиях и учреждениях и поставка продукции общественного питания', 'Деятельность столовых  при  предприятиях и учреждениях и поставка продукции общественного питания', 1444, '55', 2);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1461, '55.51', 'Деятельность столовых при предприятиях и учреждениях', 'Деятельность столовых при предприятиях и учреждениях \nЭта группировка включает: \n- изготовление   и   реализацию   кулинарной  продукции, разнообразной по дням недели, или  специальных  рационов питания  для  различных групп обслуживаемого контингента (рабочих,  школьников,  студентов,   туристов,   личного состава  вооруженных сил и других групп потребителей) по льготным ценам', 1460, '55.5', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1462, '55.52', 'Поставка продукции общественного питания', 'Поставка продукции общественного питания \nЭта группировка включает: \n- деятельность   предприятий   общественного  питания  и индивидуальных    предпринимателей    по    производству продукции   общественного   питания,   ее   доставке   и организации   потребления    различными    контингентами населения \n- поставку кулинарной продукции по заказам  потребителей на   рабочие  места,  транспортные  предприятия  в  пути следования   пассажирского   сухопутного,    воздушного, водного   транспорта,  на  дом,  обслуживание  банкетов, свадеб, приемов и др.', 1460, '55.5', 0);
