INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1464, 'Подраздел IA', 'ТРАНСПОРТ И СВЯЗЬ', '\nЭта группировка не включает: \n- капитальный  ремонт  или переоборудование транспортных средств, кроме автотранспортных, см. 35 \n- строительство,   техническое   обслуживание  и  ремонт шоссейных  и  железных   дорог,   портовых   сооружений, аэродромов, см. 45 \n- техническое  обслуживание  и  ремонт  автотранспортных средств, см. 50.20 \n- аренду  транспортных  средств   и   оборудования   без водителя или оператора, см. 71.1, 71.2', 1463, 'РАЗДЕЛ I', 5);
