INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1467, '60.10', 'Деятельность железнодорожного транспорта', 'Деятельность железнодорожного транспорта \nЭта группировка включает: \n- междугородные и международные  пассажирские  перевозки по железным дорогам \n- междугородные,   международные,     пригородные      и внутригородские грузовые перевозки по железным дорогам \n- предоставление маневровых услуг \n- перевозки грузов по подъездным путям предприятий \nЭта группировка не включает: \n- деятельность  пассажирских   и   грузовых  терминалов, транспортную  обработку   грузов,   хранение   и  прочую вспомогательную деятельность, см. 63 \n- техническое обслуживание и  мелкий  ремонт  подвижного состава, см. 63.21.1 \n- эксплуатацию железнодорожных сооружений, см. 63.21', 1466, '60.1', 2);
