INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1469, '60.10.11', 'Деятельность магистрального пассажирского железнодорожного транспорта', 'Деятельность       магистрального          пассажирского железнодорожного транспорта \nЭта группировка включает: \n- междугородные и международные  пассажирские  перевозки по железным дорогам независимо от расстояния  и  класса, а  также  перевозку  принадлежащего  пассажирам  багажа, животных и т.п.', 1468, '60.10.1', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1470, '60.10.12', 'Деятельность магистрального грузового железнодорожного транспорта', 'Деятельность магистрального  грузового  железнодорожного транспорта \nЭта группировка включает: \n- междугородные,    международные,     пригородные     и внутригородские перевозки по железным дорогам  различных грузов', 1468, '60.10.1', 0);
