INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1473, '60.21', 'Деятельность прочего сухопутного пассажирского транспорта, подчиняющегося расписанию', 'Деятельность    прочего     сухопутного    пассажирского транспорта, подчиняющегося расписанию \nЭта группировка включает: \n- деятельность,   обеспечивающую    внутригородские    и пригородные  перевозки   пассажиров   по   установленным маршрутам, подчиняющиеся расписанию,  посадку и  высадку пассажиров  на   указанных   в   расписании  остановках. Перевозки могут  осуществляться  автобусами,  трамваями, троллейбусами,   наземными    и   подземными   железными дорогами, включая метро и т.д. \n- деятельность, обеспечивающую  междугородные  перевозки (кроме  железнодорожных)   пассажиров  по  установленным маршрутам, подчиняющиеся расписанию, посадку  и  высадку пассажиров на указанных в расписании остановках \n- деятельность,   обеспечивающую   регулярные   (включая специальные)    перевозки,   осуществляемые    школьными автобусами,  экспрессами   по  маршрутам  город-аэропорт или   город-вокзал,   фуникулерами,   воздушно-канатными дорогами и т.п.', 1472, '60.2', 3);
