INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1486, '60.23', 'Деятельность прочего сухопутного пассажирского транспорта', 'Деятельность прочего      сухопутного      пассажирского транспорта \nЭта группировка включает: \n- деятельность   прочего    сухопутного    пассажирского транспорта, не подчиняющегося расписанию: \n- чартерные перевозки \n- аренду    городских   и   междугородных  автобусов  с водителем \n- предоставление  услуг по перевозке с экскурсионными и прочими целями \n- перевозки    пассажиров  транспортными  средствами  с живой тягой \nЭта группировка не включает: \n- перевозку машинами скорой помощи, см. 85.14.4', 1472, '60.2', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1487, '60.24', 'Деятельность автомобильного грузового транспорта', 'Деятельность автомобильного грузового транспорта', 1472, '60.2', 3);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1488, '60.24.1', 'Деятельность автомобильного грузового специализированного транспорта', 'Деятельность           автомобильного          грузового специализированного транспорта \nЭта группировка включает: \n- перевозку  автомобильным  грузовым  специализированным транспортом различных грузов, включая перевозку  опасных грузов: \n- замороженных или охлажденных грузов, включая перевозку в специальных автомобилях-рефрижераторах \n- наливных грузов, в  том  числе  в  цистернах,  включая сбор и перевозку молока с сельскохозяйственных ферм \n- газообразных грузов \n- грузов в контейнерах \n- сухих грузов навалом \n- автомобилей \n- животных \n- транспортирование  мусора   и    отходов,    собранных третьей стороной. В услуги транспортных фирм  обычно  не включается ответственность за  конечную  обработку  этих отходов.  Это   подтверждается   фактом,  что  обработка отходов  в  отведенных  для  этого  местах  оплачивается отдельно ответственным за их  удаление.  Примером  может служить  транспортирование  отходов  (навалом)  из  мест погрузки  к   местам  конечной  обработки.  Здесь  также классифицируется    транспортирование    навоза,    ила, загрязненной почвы \nЭта группировка также включает: \n- перевозку мебели \n- грузовые перевозки транспортными  средствами  с  живой тягой \nЭта группировка не включает: \n- деятельность  терминалов   по  транспортной  обработке грузов, см. 63.2 \n- почтовую и курьерскую деятельность, см. 64.1 \n- транспортирование  отходов   как   неотъемлемую  часть деятельности    по    сбору    отходов,   осуществляемой специализированными предприятиями, см. 90', 1487, '60.24', 0);
