INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1500, '61', 'Деятельность водного транспорта', 'Деятельность водного транспорта \nЭта группировка включает: \n- пассажирские и грузовые  перевозки  по  водным  путям, подчиняющиеся или не подчиняющиеся расписанию \n- эксплуатацию экскурсионных,  прогулочных и  туристских судов \n- эксплуатацию паромов, водных такси и т.п. \n- буксировку судов и иных плавучих средств \n- транспортирование с помощью буксиров  или  буксиров  \n- толкачей  барж,  плавучих  платформ  буровых установок и т.п. \n- аренду судов, включая лодки, с экипажем \nЭта группировка не включает: \n- деятельность  ресторанов  и баров на борту судов,  см. 55.30, 55.40 \n- транспортную   обработку   грузов,   хранение  грузов, портовые операции и прочую вспомогательную  деятельность (постановку  судна  в  док,  лоцманскую  проводку судов, погрузку  и  разгрузку   судов   посредством   лихтеров, спасание судов и т.п.), см. 63', 1464, 'Подраздел IA', 2);
