INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1593, '63.30', 'Деятельность туристических агентств', 'Деятельность туристических агентств \nЭта группировка включает: \n- деятельность   организаторов   туристических   поездок (туроператоров) \n- деятельность туристических агентов (турагентов) \n- деятельность экскурсоводов, гидов-переводчиков \n- деятельность местных туристических информационных бюро и бюро по предоставлению мест для проживания', 1592, '63.3', 4);
