INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1599, '63.40', 'Организация перевозок грузов', 'Организация перевозок грузов \nЭта группировка включает: \n- деятельность   транспортно-экспедиционных    агентств, транспортно-экспедиционное  обслуживание  грузов на всех видах транспорта \n- деятельность логистических центров и операторов \n- организацию перевозок  грузов  сухопутным,  водным или воздушным транспортом \n- прием  групповых  и   индивидуальных    партий   груза (включая подбор грузов и группировку партий груза) \n- подготовку транспортной документации и путевых листов \n- организацию  групповой  отправки  грузов   сухопутным, воздушным  или   водным  транспортом  (включая  сбор   и распределение грузов) \n- деятельность таможенных агентов \n- деятельность фрахтовых  агентов  (водного  транспорта) и экспедиторов (воздушного транспорта) \n- операции   по   транспортной  обработке  грузов,  т.е. временное упаковывание с целью  предохранения  во  время транзитной  перевозки,   распаковывание,   взятие  проб, взвешивание грузов и т.п. \nЭта группировка не включает: \n- курьерскую деятельность, см. 64.12 \n- деятельность,  связанную  с  организацией  страхования грузов, см. 67.20', 1598, '63.4', 0);
