INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1601, '64.1', 'Почтовая и курьерская деятельность', 'Почтовая и курьерская деятельность Почтовая   и    курьерская   деятельность   в   основном заключается  в  сборе  (приеме),  пересылке  и  доставке почтовой корреспонденции  (писем,  посылок  и  т.п.).  В настоящее    время    наряду      с     государственными предприятиями  в   области   почтовой  связи  существуют различные  частные   почтовые   предприятия.  В   данной группировке     классифицирована     деятельность    как национальной  почты,   так   и  ненациональных  почтовых предприятий', 1600, '64', 2);
