INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1616, '64.20.1', 'Деятельность в области телефонной связи', 'Деятельность в области телефонной связи', 1615, '64.20', 2);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1617, '64.20.11', 'Деятельность в области фиксированной телефонной связи', 'Деятельность в области фиксированной телефонной связи \nЭта группировка включает: \n- деятельность в области местной телефонной связи \n- деятельность в области внутризоновой телефонной связи \n- деятельность в области междугородной  и  международной телефонной связи \n- деятельность в области фиксированной телефонной  связи в выделенной сети связи', 1616, '64.20.1', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1618, '64.20.12', 'Деятельность в области подвижной связи', 'Деятельность в области подвижной связи \nЭта группировка включает: \n- деятельность в области связи персонального радиовызова \n- деятельность в области  подвижной  радиосвязи  в  сети связи общего пользования \n- деятельность в области подвижной радиотелефонной связи \n- деятельность   в    области    подвижной   спутниковой радиосвязи \n- деятельность  в   области   подвижной   радиосвязи   в выделенной сети связи', 1616, '64.20.1', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1619, '64.20.2', 'Деятельность в области телеграфной связи', 'Деятельность в области телеграфной связи \nЭта группировка включает: \n- деятельность   по    приему,    передаче,   обработке, хранению  и   доставке   адресату   текстовых  сообщений телеграфной связи \n- деятельность по установлению соединений для  приема  и передачи текстовых  сообщений  телеграфной  связи  между абонентскими терминалами', 1615, '64.20', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1620, '64.20.3', 'Деятельность в области оказания услуг межсистемной связи', 'Деятельность в области оказания услуг межсистемной связи \nЭта группировка включает: \n- деятельность по присоединению сетей связи \n- деятельность по пропуску трафика \n- деятельность      по      эксплуатационно-техническому обслуживанию сетей электросвязи', 1615, '64.20', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1621, '64.20.4', 'Деятельность в области передачи данных', 'Деятельность в области передачи данных \nЭта группировка включает: \n- деятельность  по   передаче   данных,  за  исключением деятельности связи по передаче данных для целей передачи голосовой информации \n- деятельность по передаче  данных  для  целей  передачи голосовой информации', 1615, '64.20', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1622, '64.20.5', 'Деятельность в области оказания телематических услуг связи', 'Деятельность в  области  оказания  телематических  услуг связи \nЭта группировка включает: \n- деятельность в  области  оказания  услуг  по  передаче факсимильных сообщений \n- деятельность в  области  оказания  услуг  по  передаче сообщений электронной почты \n- деятельность   в    области    оказания    услуг    по предоставлению   доступа   к   информации    мировых   и региональных  информационно-телекоммуникационных  сетей, в том числе к сети Интернет', 1615, '64.20', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1623, '64.20.6', 'Деятельность в области кабельного вещания, эфирного вещания и проводного радиовещания', 'Деятельность  в  области  кабельного  вещания,  эфирного вещания и проводного радиовещания \nЭта группировка включает: \n- деятельность в области кабельного вещания \n- деятельность в области эфирного вещания \n- деятельность в области проводного радиовещания', 1615, '64.20', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1624, '64.20.7', 'Прочая деятельность в области электросвязи', 'Прочая деятельность в области электросвязи \nЭта группировка включает: \n- деятельность   в   области     оказания    услуг    по предоставлению каналов связи \n- прочая  деятельность   в   области   оказания    услуг электросвязи', 1615, '64.20', 0);
