INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1627, '65', 'Финансовое посредничество', 'Финансовое посредничество \nЭта группировка включает: \n- деятельность,     связанную      с     получением    и перераспределением финансовых  средств,  кроме  средств, предназначенных  для  целей  страхования  (обязательного социального страхования) \nЭта группировка не включает: \n- страхование и деятельность по пенсионному обеспечению, см. 66 \n- обязательное социальное страхование, см. 75.30 \n- управление имуществом, находящимся  в  государственной собственности, см. 75.11.8', 1626, 'Подраздел JA', 2);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1628, '65.1', 'Денежное посредничество', 'Денежное посредничество \nЭта группировка включает: \n- деятельность  банков  и  других  финансово \n- кредитных учреждений, принимающих депозиты', 1627, '65', 2);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1629, '65.11', 'Деятельность Центрального банка Российской Федерации', 'Деятельность Центрального банка Российской Федерации', 1628, '65.1', 2);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1630, '65.11.1', 'Разработка и проведение во взаимодействии с Правительством Российской Федерации единой государственной денежно-кредитной политики', 'Разработка   и     проведение    во   взаимодействии   с Правительством     Российской      Федерации      единой государственной денежно-кредитной политики \nЭта группировка включает: \n- предоставление кредитов \n- привлечение депозитов \n- депонирование обязательных резервов \n- разработку и реализацию валютной политики', 1629, '65.11', 2);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1631, '65.11.11', 'Осуществление эмиссии наличных денег и организация наличного денежного обращения', 'Осуществление  эмиссии   наличных  денег  и  организация наличного денежного обращения \nЭта группировка включает: \n- получение отчетов и сводок по денежному обращению \n- анализ состояния денежного обращения \n- прогнозирование кассовых оборотов \n- учет эмиссионных операций в регионах \n- учет резервных фондов \n- учет пересылки ценностей \n- прием,  выдачу,    обработку   и   хранение   денежной наличности', 1630, '65.11.1', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1632, '65.11.12', 'Выполнение функции кредитора последней инстанции для кредитных организаций, организация системы их рефинансирования', 'Выполнение функции  кредитора  последней  инстанции  для кредитных   организаций,    организация    системы    их рефинансирования \nЭта группировка включает: \n- выдачу кредитов, обеспеченных залогом  государственных ценных бумаг', 1630, '65.11.1', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1633, '65.11.9', 'Прочая деятельность Центрального банка Российской Федерации', 'Прочая  деятельность   Центрального   банка   Российской Федерации \nЭта группировка включает: \n- организацию и проведение операций  на  открытом  рынке ГКО-ОФЗ \n- валютное регулирование и валютный контроль \n- контроль операций по кассовому исполнению бюджета \n- прочую  деятельность  Центрального  банка   Российской Федерации, не включенную в другие группировки', 1629, '65.11', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1634, '65.12', 'Прочее денежное посредничество', 'Прочее денежное посредничество \nЭта группировка включает: \n- деятельность   денежно    \n-    кредитных    учреждений (коммерческих  банков  и  прочих  кредитных  учреждений, имеющих лицензии на осуществление банковских  операций): расчетные  и кассовые операции;  операции по вкладам,  в том числе операции по оптовым вкладам и вкладам в других банках; предоставление кредитов; деятельность, связанную  с  кредитными  карточками  и  с эмиссией денег; валютные операции \n- деятельность,  связанную  с   почтовыми   безналичными расчетами  (жирорасчетами)  и  почтово \n- сберегательными банками \nЭта группировка не включает: \n- продажу коллекционных почтовых марок, см. 52.48.36', 1628, '65.1', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1635, '65.2', 'Прочее финансовое посредничество', 'Прочее финансовое посредничество \nЭта группировка включает: \n- деятельность   кредитных  учреждений,  не  принимающих депозиты (финансовых  корпораций  и  финансовых  фондов, инвестиционных  фирм,  инновационных  и лизинговых фирм, благотворительных и спонсорских фондов)', 1627, '65', 3);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1636, '65.21', 'Финансовый лизинг', 'Финансовый лизинг \nЭта группировка включает: \n- деятельность лизинговых фирм и компаний,  занимающихся сделками в области лизингового кредитования \nЭта группировка не включает: \n- операционный лизинг в соответствии  с  видом  товаров, предоставляемых в аренду, см. 71', 1635, '65.2', 2);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1637, '65.21.1', 'Финансовый лизинг племенных животных', 'Финансовый лизинг племенных животных', 1636, '65.21', 0);
