INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1639, '65.22', 'Предоставление кредита', 'Предоставление кредита \nЭта группировка включает: \n- предоставление кредита учреждениями,  не  принимающими депозиты, в том числе предоставление денежных  ссуд  вне банковской системы \nЭта группировка не включает: \n- предоставление кредитов Центральным банком  Российской Федерации, см. 65.11.1 \n- предоставление кредитов банками и  другими  кредитными учреждениями, принимающими депозиты, см. 65.12', 1635, '65.2', 9);
