INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1643, '65.22.4', 'Предоставление кредитов на покупку домов специализированными учреждениями, не принимающими депозиты', 'Предоставление     кредитов     на     покупку     домов специализированными учреждениями,    не     принимающими депозиты', 1639, '65.22', 0);
