INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1645, '65.22.6', 'Предоставление ломбардами краткосрочных кредитов под залог движимого имущества', 'Предоставление  ломбардами  краткосрочных  кредитов  под залог движимого имущества \nЭта группировка включает: \n- деятельность   ломбардов   в    области    финансового посредничества по предоставлению населению краткосрочных кредитов под залог движимого имущества \nЭта группировка не включает: \n- продажу   ломбардами  принятого  от граждан в залог  и не выкупленного движимого имущества: \n- для автотранспортных средств, см. 50.10 \n- для других товаров, см. 52.50', 1639, '65.22', 0);
