INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1649, '65.23', 'Финансовое посредничество, не включенное в другие группировки', 'Финансовое   посредничество,   не  включенное  в  другие группировки \nЭта группировка включает: \n- финансовое посредничество,  связанное,  в основном,  с размещением  финансовых  средств,  кроме  предоставления займов \nЭта группировка не включает: \n- финансовый лизинг, см. 65.21 \n- операционный лизинг, см. 71', 1635, '65.2', 5);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1650, '65.23.1', 'Капиталовложения в ценные бумаги', 'Капиталовложения в ценные бумаги \nЭта группировка включает: \n- капиталовложения в акции, облигации,  векселя,  ценные бумаги акционерных фондов и паевых инвестиционных фондов и т.п.', 1649, '65.23', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1651, '65.23.2', 'Деятельность дилеров', 'Деятельность дилеров \nЭта группировка включает: \n- операции   с   ценными   бумагами,  осуществляемые  за собственный счет \nЭта группировка не включает: \n- операции с ценными бумагами по поручению  других  лиц, см. 67.12', 1649, '65.23', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1652, '65.23.3', 'Капиталовложения в собственность', 'Капиталовложения в собственность \nЭта группировка включает: \n- капиталовложения в  собственность,  осуществляемые,  в основном,   за   счет   других  финансовых  посредников, например трасткомпаний \nЭта группировка не включает: \n- покупку,  продажу и аренду недвижимого имущества,  см. 70', 1649, '65.23', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1653, '65.23.4', 'Заключение свопов, опционов и других биржевых сделок', 'Заключение свопов, опционов и других биржевых сделок', 1649, '65.23', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1654, '65.23.5', 'Деятельность холдинг-компаний в области финансового посредничества', 'Деятельность  холдинг  \n-  компаний в области финансового посредничества \nЭта группировка не включает: \n- деятельность по управлению холдинг \n-  компаниями,  см. 74.15.2', 1649, '65.23', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1655, '66', 'Страхование', 'Страхование \nЭта группировка включает: \n- долгосрочное и краткосрочное покрытие страховых рисков с элементом сбережения или без него \nЭта группировка не включает: \n- вспомогательную  деятельность   в  сфере  страхования: деятельность страховых агентов, специалистов  по  оценке страхового риска и  убытков,  специалистов  по  расчетам оценки страховой  вероятности (актуариев),  деятельность распорядителей спасательными работами, см. 67.20 \n- социальное обеспечение, см. 75.30', 1626, 'Подраздел JA', 1);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1656, '66.0', 'Страхование', 'Страхование', 1655, '66', 3);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1657, '66.01', 'Страхование жизни', 'Страхование жизни \nЭта группировка включает: \n- деятельность по страхованию  и  перестрахованию  жизни с существенным элементом сбережения или без него', 1656, '66.0', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1658, '66.02', 'Добровольное пенсионное страхование', 'Добровольное пенсионное страхование', 1656, '66.0', 2);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1659, '66.02.1', 'Деятельность по добровольному пенсионному страхованию', 'Деятельность по добровольному  пенсионному  страхованию \nЭта группировка включает: \n- деятельность  в   области  финансового  посредничества страховых касс, компаний, обществ и  т.п.,  связанную  с получением  и   перераспределением   страховых  взносов, обеспечивающих дополнительное пенсионное страхование \nЭта группировка не включает: \n- управление  активами    негосударственных   пенсионных фондов, см. 67.12.2 \n- управление  социальными   программами,  финансируемыми главным образом за счет государственных источников и  не предусматривающими собственного вклада, см. 75.12 \n- обязательное пенсионное страхование, см. 75.30', 1658, '66.02', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1660, '66.02.2', 'Страхование ренты', 'Страхование ренты', 1658, '66.02', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1661, '66.03', 'Прочие виды страхования', 'Прочие виды страхования \nЭта группировка включает: \n- страхование  и   перестрахование,   не  относящееся  к страхованию жизни: добровольное медицинское страхование; страхование  от  несчастного случая, пожара; страхование от  болезней; страхование  имущества;  автотранспортное, морское и авиационное страхование,  страхование  средств транспорта;  страхование  на  случай  денежных  убытков; страхование ответственности и прочие виды страхования', 1656, '66.0', 6);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1662, '66.03.1', 'Добровольное медицинское страхование', 'Добровольное медицинское страхование \nЭта группировка включает: \n- добровольное медицинское  страхование,  осуществляемое за счет прибыли предприятий  и  организаций  или  личных средств граждан  путем  заключения  страховых  договоров на получение дополнительного медицинского  обслуживания, сверх установленного программами социального страхования \nЭта группировка не включает: \n- деятельность,  связанную   с  управлением  социальными программами в области здравоохранения, см. 75.12 \n- страхование  от  несчастных  случаев  и  болезней, см. 66.03.4 \n- деятельность, связанную с предоставлением  пособий  по болезни, материнству, по случаю потери трудоспособности, государственное медицинское страхование, см. 75.30 \n- предоставление социальной  помощи  и социальных услуг, см. 85.3', 1661, '66.03', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1663, '66.03.2', 'Страхование имущества', 'Страхование имущества \nЭта группировка включает: \n- страхование средств наземного, водного,  воздушного  и космического транспорта,  грузов,  домашнего  имущества, оборудования, товарно-материальных ценностей и т.д. \nЭта группировка не включает: \n- страхование рисков, см. 66.03.5', 1661, '66.03', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1664, '66.03.3', 'Страхование ответственности', 'Страхование ответственности \nЭта группировка включает: \n- страхование  гражданской  ответственности:  владельцев автотранспортных  средств,  перевозчиков,  предприятий \n- источников повышенной опасности \n- страхование профессиональной ответственности \n- страхование    ответственности     за     неисполнение обязательств, в том числе договорных обязательств', 1661, '66.03', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1665, '66.03.4', 'Страхование от несчастных случаев и болезней', 'Страхование от несчастных случаев и болезней \nЭта группировка не включает: \n- добровольное медицинское страхование, см. 66.03.1', 1661, '66.03', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1666, '66.03.5', 'Страхование рисков', 'Страхование рисков', 1661, '66.03', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1667, '66.03.9', 'Прочие виды страхования, не включенные в другие группировки', 'Прочие  виды  страхования,  не   включенные   в   другие группировки', 1661, '66.03', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1668, '67', 'Вспомогательная деятельность в сфере финансового посредничества и страхования', 'Вспомогательная   деятельность    в   сфере  финансового посредничества и страхования \nЭта группировка включает: \n- предоставление   услуг,   относящихся   к  финансовому посредничеству  или  тесно  связанных   с   ним,   кроме предоставления самих услуг по финансовому посредничеству', 1626, 'Подраздел JA', 2);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1669, '67.1', 'Вспомогательная деятельность в сфере финансового посредничества', 'Вспомогательная деятельность   в    сфере    финансового посредничества', 1668, '67', 3);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1670, '67.11', 'Управление финансовыми рынками', 'Управление финансовыми рынками \nЭта группировка включает: \n- функционирование  финансовых   рынков,  организацию  и регулирование их деятельности', 1669, '67.1', 1);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1671, '67.11.1', 'Деятельность фондовых, товарных, валютных и валютно фондовых бирж', 'Деятельность  фондовых,  товарных,  валютных и валютно \n- фондовых бирж \nЭта группировка не включает: \n- биржевые операции с фондовыми ценностями, см. 67.12 \n- депозитарную деятельность, см. 67.13.51 \n- государственное регулирование  и  контроль  финансовых рынков, см. 75.13', 1670, '67.11', 5);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1672, '67.11.11', 'Деятельность по организации торговли на финансовых рынках', 'Деятельность   по    организации  торговли на финансовых рынках', 1671, '67.11.1', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1673, '67.11.12', 'Деятельность по ведению реестра владельцев ценных бумаг (деятельность регистраторов)', 'Деятельность по ведению реестра владельцев  ценных бумаг (деятельность регистраторов)', 1671, '67.11.1', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1674, '67.11.13', 'Деятельность по обеспечению эффективности функционирования финансовых рынков', 'Деятельность     по      обеспечению       эффективности функционирования финансовых рынков \nЭта группировка включает: \n- обеспечение  условий для деятельности профессиональных участников рынка, защиту интересов владельцев финансовых ценностей и инвесторов, установление правил и стандартов проведения операций с фондовыми ценностями', 1671, '67.11.1', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1675, '67.11.14', 'Деятельность по определению взаимных обязательств (клиринг)', 'Деятельность  по   определению   взаимных   обязательств (клиринг)', 1671, '67.11.1', 0);
