INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1677, '67.12', 'Биржевые операции с фондовыми ценностями и управление активами', 'Биржевые операции с фондовыми  ценностями  и  управление активами \nЭта группировка включает: \n- осуществление  операций   на   финансовых  рынках   по поручению других лиц и связанную с этим деятельность \nЭта группировка не включает: \n- дилерскую деятельность по операциям  на  рынке  ценных бумаг, осуществляемым от своего имени и  за  свой  счет, см. 65.23 \n- деятельность  по  ведению  реестра  владельцев  ценных бумаг (деятельность регистраторов), см. 67.11.12 \n- деятельность,  связанную   с   управлением   фондовыми рынками, организацией торговли на рынке ценных  бумаг  и обеспечением эффективности функционирования рынка ценных бумаг, см. 67.11 \n- депозитарную деятельность, см. 67.13.51 \n- предоставление   брокерских     услуг   по   ипотечным операциям, см. 67.13.1', 1669, '67.1', 3);
