INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1679, '67.12.2', 'Деятельность по управлению ценными бумагами', 'Деятельность по управлению ценными бумагами \nЭта группировка также включает: \n- деятельность по управлению активами  инвестиционных  и негосударственных пенсионных фондов,  страховых компаний и иными активами', 1677, '67.12', 0);
