INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1681, '67.13', 'Прочая вспомогательная деятельность в сфере финансового посредничества', 'Прочая вспомогательная  деятельность в сфере финансового посредничества \nЭта группировка включает: \n- все виды деятельности,  являющиеся вспомогательными по отношению к финансовому посредничеству,  не включенные в другие группировки \nЭта группировка не включает: \n- консультирование  по  вопросам финансового управления, кроме консультирования по вопросам налогообложения,  см. 74.14 \n- перевозку ценностей, см. 74.60', 1669, '67.1', 5);
