INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1695, 'РАЗДЕЛ K', 'ОПЕРАЦИИ С НЕДВИЖИМЫМ ИМУЩЕСТВОМ, АРЕНДА И ПРЕДОСТАВЛЕНИЕ УСЛУГ', 'УСЛУГ Этот раздел включает: \n- операции с недвижимым имуществом \n-  аренду  машин  и  оборудования без оператора и прокат прочих бытовых изделий и предметов личного пользования \n- деятельность, связанную с вычислительной техникой \n- научные исследования и разработки \n-  деятельность  в области права, бухгалтерского учета и аудита,   консультирование   по   вопросам  коммерческой деятельности и управления предприятием \n-   деятельность   в   области  архитектуры,  инженерных изысканий  и  предоставление  технических консультаций в этих областях \n- испытания и анализ \n- рекламную деятельность \n- наем рабочей силы и подбор персонала \n- проведение расследований и обеспечение безопасности \n- чистку и уборку помещений, оборудования и транспортных средств. Перечисленные  виды  деятельности  сосредоточены главным образом  в  сфере  предпринимательской  деятельности. Но многие  из них могут относиться и к домашним хозяйствам, например  прокат  бытовых  изделий  и  предметов личного пользования,   деятельность   в   области   баз  данных, деятельность  в  области  права,  предоставление услуг в области    проведения    расследований   и   обеспечения безопасности, оформления помещений или фотографии', NULL, NULL, 1);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1696, 'Подраздел KA', 'ОПЕРАЦИИ С НЕДВИЖИМЫМ ИМУЩЕСТВОМ, АРЕНДА И ПРЕДОСТАВЛЕНИЕ УСЛУГ', 'ПРЕДОСТАВЛЕНИЕ УСЛУГ Этот раздел включает: \n- операции с недвижимым имуществом \n- аренду машин и оборудования без  оператора  и   прокат прочих бытовых изделий и предметов личного пользования \n- деятельность, связанную с вычислительной техникой \n- научные исследования и разработки \n- деятельность в области права,  бухгалтерского учета  и аудита,   консультирование   по  вопросам   коммерческой деятельности и управления предприятием \n- деятельность   в   области   архитектуры,   инженерных изысканий и предоставление технических  консультаций   в этих областях \n- испытания и анализ \n- рекламную деятельность \n- наем рабочей силы и подбор персонала \n- проведение расследований и обеспечение безопасности \n- чистку   и   уборку   помещений,    оборудования     и транспортных средств \n- предоставление различных видов услуг потребителям', 1695, 'РАЗДЕЛ K', 5);
