INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1699, '70.11', 'Подготовка к продаже собственного недвижимого имущества', 'Подготовка к продаже собственного недвижимого  имущества \nЭта группировка включает: \n- разработку своими силами и за свой счет   коммерческих проектов,    связанных    с    недвижимым     имуществом (объединение финансовых,  технических средств  и людских ресурсов   для   реализации   проектов,   связанных    с недвижимым имуществом,  предназначенным для  жилья   или других целей), для его дальнейшей продажи \nЭта группировка не включает: \n- строительные   работы,   включающие    проектирование, выполняемые строительными организациями, см. 45.2 \n- проектирование   и   разработку  чертежей  в   области архитектуры, см. 74.20.11', 1698, '70.1', 2);
