INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1718, '70.32', 'Управление недвижимым имуществом', 'Управление недвижимым имуществом \nЭта группировка включает: \n- деятельность  по   обслуживанию  зданий  и  сооружений (включая предоставление  услуг  и  организацию  процесса обслуживания), относящуюся к: \n- эксплуатации оборудования \n- очистке, содержанию  и  уходу  за  помещениями  внутри зданий \n- проверке  и   регулированию  тепловых,  вентиляционных систем и систем кондиционирования воздуха \n- проведению мелких ремонтных работ', 1710, '70.3', 3);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1719, '70.32.1', 'Управление эксплуатацией жилого фонда', 'Управление эксплуатацией жилого фонда \nЭта группировка включает также: \n- деятельность по надзору за состоянием и  эксплуатацией жилого фонда \n- деятельность  учреждений  по  сбору арендной  платы за эксплуатацию жилого фонда', 1718, '70.32', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1720, '70.32.2', 'Управление эксплуатацией нежилого фонда', 'Управление эксплуатацией нежилого фонда \nЭта группировка включает также: \n- деятельность  по надзору за состоянием и эксплуатацией нежилого фонда и земельных участков \n- деятельность  учреждений  по  сбору  арендной платы за эксплуатацию нежилого фонда \n- деятельность  учреждений  по  сбору  арендной платы за землю', 1718, '70.32', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1721, '70.32.3', 'Деятельность по учету и технической инвентаризации недвижимого имущества', 'Деятельность по   учету   и  технической  инвентаризации недвижимого имущества \nЭта группировка включает: \n- учет и техническую инвентаризацию жилого фонда \n- учет и техническую инвентаризацию нежилого фонда \n- составление и ведение земельных кадастров  и  балансов земель', 1718, '70.32', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1722, '71', 'Аренда машин и оборудования без оператора; прокат бытовых изделий и предметов личного пользования', 'Аренда  машин  и  оборудования  без  оператора;   прокат бытовых изделий и предметов личного пользования \nЭта группировка включает: \n- аренду и операционный лизинг,  который является формой долгосрочной аренды, без оператора (водителя, машиниста, пилота, экипажа и т.п.) \nЭта группировка не включает: \n- финансовый    лизинг,    который    является    формой предоставления кредита, см. 65.21', 1696, 'Подраздел KA', 4);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1723, '71.1', 'Аренда легковых автомобилей', 'Аренда легковых автомобилей', 1722, '71', 1);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1724, '71.10', 'Аренда легковых автомобилей', 'Аренда легковых автомобилей \nЭта группировка включает: \n- аренду легковых автомобилей и легких  автофургонов  до 3,5 т без водителя \nЭта группировка не включает: \n- аренду легковых автомобилей с водителем, см. 60.22 \n- финансовый лизинг, см. 65.21', 1723, '71.1', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1725, '71.2', 'Аренда прочих транспортных средств и оборудования', 'Аренда прочих транспортных средств и оборудования', 1722, '71', 3);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1726, '71.21', 'Аренда прочих сухопутных транспортных средств и оборудования', 'Аренда  прочих   сухопутных   транспортных   средств   и оборудования \nЭта группировка включает: \n- аренду грузовых контейнеров \n- аренду поддонов \nЭта группировка не включает: \n- аренду  прочих   сухопутных   транспортных  средств  с оператором, см. 60.2 \n- финансовый лизинг, см. 65.21 \n- аренду легковых автомобилей без водителя, см. 71.10 \n- аренду жилых или служебных передвижных  помещений, см. 71.32 \n- прокат велосипедов, см. 71.40.4', 1725, '71.2', 2);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1727, '71.21.1', 'Аренда прочего автомобильного транспорта и оборудования', 'Аренда прочего автомобильного транспорта и оборудования \nЭта группировка включает: \n- аренду  грузовых   автомобилей,  тягачей,  прицепов  и полуприцепов без водителя \n- аренду мотоциклов, автофургонов и т.п. без водителя', 1726, '71.21', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1728, '71.21.2', 'Аренда железнодорожного транспорта и оборудования', 'Аренда железнодорожного транспорта и оборудования \nЭта группировка включает: \n- аренду   железнодорожного   подвижного   состава   без машиниста', 1726, '71.21', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1729, '71.22', 'Аренда водных транспортных средств и оборудования', 'Аренда водных транспортных средств и оборудования \nЭта группировка включает: \n- аренду водных транспортных средств (торговых и  прочих судов) без экипажа \nЭта группировка не включает: \n- аренду водных транспортных средств с экипажем, см. 61 \n- финансовый лизинг, см. 65.21 \n- прокат прогулочных катеров, см. 71.40.4', 1725, '71.2', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1730, '71.23', 'Аренда воздушных транспортных средств и оборудования', 'Аренда воздушных транспортных средств и оборудования \nЭта группировка включает: \n- аренду  самолетов,  вертолетов  и   других   воздушных транспортных средств без экипажа \nЭта группировка не включает: \n- аренду воздушных транспортных средств с экипажем,  см. 62.20.3 \n- финансовый лизинг, см. 65.21', 1725, '71.2', 0);
