INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1757, '71.40.9', 'Прокат прочих бытовых изделий и предметов личного пользования для домашних хозяйств, предприятий и организаций, не включенных в другие группировки', 'Прокат прочих  бытовых  изделий  и   предметов   личного пользования   для   домашних   хозяйств,  предприятий  и организаций, не включенных в другие группировки \nЭта группировка включает: \n- прокат текстильных изделий,  одежды, обуви; изделий из керамики и стекла, кухонной и столовой посуды; ювелирных изделий; театральных декораций и костюмов; книг, газет и журналов; машин   и  оборудования  для  самостоятельного изготовления  поделок;   цветов   и   других   растений; различной домашней утвари', 1750, '71.40', 0);
