INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1783, '74.13', 'Маркетинговые исследования и выявление общественного мнения', 'Маркетинговые  исследования  и  выявление  общественного мнения', 1778, '74.1', 2);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1784, '74.13.1', 'Маркетинговые исследования', 'Маркетинговые исследования \nЭта группировка включает: \n- исследования   характеристик   рынка   и    внутренних действительных     и     потенциальных      возможностей производственной или посреднической  деятельности  фирмы с целью анализа  структуры  и  закономерностей  динамики рынка и обоснования  мероприятий  по  более  эффективной адаптации производства, технологий  и  структуры  фирмы, а также выводимой фирмой на рынок  продукции  или  услуг к спросу и требованиям конечного потребителя', 1783, '74.13', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1785, '74.13.2', 'Деятельность по изучению общественного мнения', 'Деятельность по изучению общественного мнения \nЭта группировка включает: \n- изучение   общественного   мнения   по   политическим, экономическим  и  социальным  вопросам  и статистический анализ результатов', 1783, '74.13', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1786, '74.14', 'Консультирование по вопросам коммерческой деятельности и управления', 'Консультирование по вопросам коммерческой деятельности и управления \nЭта группировка включает: \n- консультирование по  вопросам  управления  финансовыми ресурсами   предприятия,   кроме   консультирования   по вопросам налогообложения,  автоматизация  бухгалтерского учета и внедрение информационных технологий \n- оценку стоимости  имущества,  оценку  предприятий  как имущественных комплексов,  а  также  предпринимательских рисков \n- консультирование по вопросам управления маркетингом \n- консультирование по вопросам управления персоналом \n- оценку стоимости  имущества,  оценку  предприятий  как имущественных  комплексов,  а  также предпринимательских рисков \n- консультирование  по  вопросам  управления  в  области сельского   хозяйства,     например     консультирование агрономами и экономистами фермеров и т.п. \n- предоставление   услуг   по    обеспечению    связей с общественностью \n- разработку   и   анализ    инвестиционных    проектов, составление бизнес-планов \n-   руководство     проектами,    кроме    строительных: координацию      и       надзор     за     расходованием ресурсов,   подготовку    графиков   выполнения   работ, координацию работы субподрядчиков, контроль за качеством выполняемых работ и т.п. \n- предоставление прочих услуг, связанных  с  управлением предприятием \nЭта группировка не включает: \n- руководство проектами  в  области  строительства,  см. 74.20.1', 1778, '74.1', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1787, '74.15', 'Деятельность по управлению финансово-промышленными группами и холдинг-компаниями', 'Деятельность по  управлению  финансово  \n-  промышленными группами и холдинг \n- компаниями', 1778, '74.1', 2);
