INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1789, '74.15.2', 'Деятельность по управлению холдинг-компаниями', 'Деятельность по управлению холдинг-компаниями \nЭта группировка включает: \n- деятельность  управляющих   холдинг-компаний,  которые занимаются главным образом хранением ценных  бумаг  (или других финансовых  активов)  компаний  и  предприятий  с целью владения контрольным пакетом акций или влияния  на управленческие решения \nЭта группировка также включает: \n- деятельность    головных    офисов,   централизованной администрации и  тому  подобных  подразделений,  которые руководят другими хозяйствующими субъектами  компании  и осуществляют наблюдение за их  деятельностью  и  которые обычно осуществляют стратегическое  или  организационное планирование, а  также  принимают  участие  в  процессах выработки решений компанией или предприятием \nЭта группировка не включает: \n- деятельность холдинг-компаний  в  области  финансового посредничества, см. 65.23.5', 1787, '74.15', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1790, '74.2', 'Деятельность в области архитектуры; инженернотехническое проектирование; геолого-разведочные и геофизические работы; геодезическая и картографическая деятельность; деятельность в области стандартизации и метрологии; деятельность в области гидрометеорологии и смежных с ней областях; виды деятельности, связанные с решением технических задач, не включенные в другие группировки', 'Деятельность в   области   архитектуры;   инженерно    \n- техническое проектирование;   геолого  \n-  разведочные  и геофизические работы;  геодезическая  и картографическая деятельность;  деятельность в области  стандартизации  и метрологии;  деятельность  в области гидрометеорологии и смежных с ней областях;  виды деятельности,  связанные с решением  технических  задач,  не  включенные  в  другие группировки', 1777, '74', 1);
