INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1793, '74.20.11', 'Архитектурная деятельность', 'Архитектурная деятельность Эта  группировка  включает  виды  деятельности,  имеющие целью  создание     архитектурного    объекта   (здания, сооружения,   комплекса   зданий   или   сооружений,  их интерьера, объектов  благоустройства,  ландшафтного  или садово-паркового искусства): \n- создание архитектурного проекта \n- координацию  разработки   всех   разделов    проектной документации   для   строительства,    реставрации   или реконструкции \n- авторский  надзор   за  строительством  архитектурного объекта \n- организацию профессиональной деятельности архитекторов', 1792, '74.20.1', 0);
