INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1813, '74.20.51', 'Деятельность наблюдательной гидрометеорологической сети', 'Деятельность наблюдательной гидрометеорологической сети \nЭта группировка включает: \n- получение     и       хранение      метеорологических, климатических,     аэрологических,      гидрологических, океанологических, агрометеорологических характеристик  и параметров \n- получение и хранение  данных  об  уровнях  загрязнения окружающей среды', 1812, '74.20.5', 0);
