INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1828, '74.30.7', 'Технический контроль автомобилей: периодический технический осмотр легковых и грузовых автомобилей, мотоциклов, автобусов и других автотранспортных средств', 'Технический контроль      автомобилей:     периодический технический  осмотр  легковых  и  грузовых  автомобилей, мотоциклов, автобусов и других автотранспортных средств', 1821, '74.30', 0);
