INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1832, '74.40', 'Рекламная деятельность', 'Рекламная деятельность \nЭта группировка включает: \n- подготовку и размещение рекламы, например  на  афишных тумбах, рекламных щитах, стендах для афиш и  объявлений, в витринах, в демонстрационных залах, размещение рекламы на автомобилях и автобусах и т.п. \n- рекламирование в средствах массовой  информации  путем продажи времени и места для рекламы \n- воздушную рекламу \n- распространение  или   адресную   рассылку   рекламных материалов, доставку рекламных образцов \n- предоставление места для рекламы \nЭта группировка не включает: \n- полиграфическое исполнение рекламных  материалов,  см. 22.22 \n- маркетинговые исследования, см. 74.13.1 \n- деятельность по связям с общественностью, см. 74.14 \n- рекламную фотосъемку, см. 74.81 \n- подготовку рекламных сообщений для радио,  телевидения и кино, см. 92', 1831, '74.4', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1833, '74.5', 'Трудоустройство и подбор персонала', 'Трудоустройство и подбор персонала', 1777, '74', 1);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1834, '74.50', 'Трудоустройство и подбор персонала', 'Трудоустройство и подбор персонала \nЭта группировка включает: \n- поиск персонала, отбор кандидатов и  распределение  по местам    работы.    Такие    услуги     предоставляются потенциальным работодателям и  могут  включать  описание работ,  подлежащих   исполнению   (например, составление должностных   инструкций);    отбор    и    тестирование претендентов на получение работы; проверку  рекомендаций и т.п. \n- трудоустройство.   Такие     услуги    предоставляются потенциальным  работникам   и   могут   включать  подбор вакансий, составление  резюме,  определение  склонностей и  способностей   при   помощи  психологических  тестов, подготовку к собеседованию с работодателем,  организацию собеседований и т.п. \n- деятельность   по     поиску     и     трудоустройство высококвалифицированных кадров \n- деятельность  по   найму   временной   рабочей   силы: обеспечение  на  подрядной  основе  предприятий  наемной рабочей силой, преимущественно для выполнения  временной работы,   агентством,   которое    и    выплачивает   им вознаграждение за выполняемую работу \n- вывод работников  за  штат предприятия  (аустстаффинг) для  снижения   административных   рисков   и  издержек, связанных с персоналом \nЭта группировка не включает: \n- деятельность подрядчиков по найму сельскохозяйственных рабочих, см. 01.4 \n- деятельность театральных  и  артистических  агентов  и агентств по подбору персонала, см. 74.87 \n- деятельность по подбору актеров на  роли  в  кино,  на телевидении, в театре, см. 92.72', 1833, '74.5', 2);
