INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1836, '74.50.2', 'Предоставление услуг по подбору персонала', 'Предоставление услуг по подбору персонала', 1834, '74.50', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1837, '74.6', 'Проведение расследований и обеспечение безопасности', 'Проведение расследований и обеспечение безопасности', 1777, '74', 1);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1838, '74.60', 'Проведение расследований и обеспечение безопасности', 'Проведение расследований и обеспечение безопасности \nЭта группировка включает: \n- перевозку ценностей \n- деятельность телохранителей \n- деятельность  уличных  патрулей,  охранников, сторожей и  вахтеров   для   многоквартирных  домов,  учреждений, фабрик,  строительных   площадок,   гостиниц,   театров, концертных и  танцевальных  залов,  стадионов,  торговых центров и т.п. \n- деятельность  по   обеспечению  безопасности  в  сфере общественного транспорта, как, например, проверка багажа и  пассажиров  в  аэропортах,  а  также патрулирование в железнодорожных поездах и в метро \n- деятельность сотрудников охранных служб в магазинах \n- обеспечение  работы   служебных   телефонов  и  других устройств  для   дистанционного  контроля  или  проверки работы промышленного оборудования \n- предварительную проверку сигналов тревоги (на  предмет принятия  решения  о  том,  не   произошло   ли  ложного срабатывания) и вызов, в случае необходимости,  полиции, пожарной команды или скорой помощи \n- консультирование   по   вопросам    безопасности    на производстве, в частном секторе, в общественных  местах, включая проверку на безопасность \n- уничтожение  информации на  любых  носителях  в  целях обеспечения безопасности \n- деятельность частных агентов \nЭта группировка не включает: \n- установку систем сигнализации, см. 45.31 \n- расследования в связи со страхованием, см. 67.20 \n- деятельность  федеральных  и  территориальных  органов служб охраны и безопасности,  а  также  деятельность  по охране общественного порядка, осуществляемую  войсковыми казачьими обществами, см. 75.24', 1837, '74.6', 2);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1839, '74.60.1', 'Оценка уязвимости объектов транспортной инфраструктуры и транспортных средств от актов незаконного вмешательства от 17.06.2011 N 135-ст)', 'Оценка уязвимости объектов транспортной инфраструктуры и транспортных средств от актов незаконного вмешательства \nЭта группировка включает: \n- оценку уязвимости объектов транспортной инфраструктуры и   транспортных    средств    от    актов   незаконного вмешательства,       проводимых      специализированными организациями    в   области   обеспечения  транспортной безопасности от 17.06.2011 N 135-ст)', 1838, '74.60', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1840, '74.60.2', 'Проведение расследований и обеспечение безопасности, кроме оценки уязвимости объектов транспортной инфраструктуры и транспортных средств от актов незаконного вмешательства от 17.06.2011 N 135-ст)', 'Проведение  расследований  и  обеспечение  безопасности, кроме    оценки    уязвимости    объектов   транспортной инфраструктуры    и   транспортных  средств   от   актов незаконного вмешательства \nЭта группировка не включает: \n- оценку уязвимости объектов транспортной инфраструктуры и    транспортных    средств    от   актов   незаконного вмешательства,       проводимых      специализированными организациями   в   области   обеспечения   транспортной безопасности, см. 74.60.1 от 17.06.2011 N 135-ст)', 1838, '74.60', 0);
