INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1842, '74.70', 'Чистка и уборка производственных и жилых помещений, оборудования и транспортных средств', 'Чистка и  уборка  производственных  и  жилых  помещений, оборудования и транспортных средств', 1841, '74.7', 3);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1843, '74.70.1', 'Чистка и уборка производственных и жилых помещений и оборудования', 'Чистка и уборка производственных  и  жилых  помещений  и оборудования \nЭта группировка включает: \n- уборку внутренних  помещений в зданиях  всех  типов, в том числе в учреждениях, офисах, на  фабриках,  заводах, в  магазинах,  институтах   и   других   коммерческих  и профессиональных учреждениях и в  многоквартирных  жилых домах \n- мытье окон \n- чистку   печных    труб,   каминов,    плит,    печей, мусоросжигателей,   бойлеров,    вентиляционных    шахт, вытяжных вентиляторов \n- чистку производственных машин и оборудования \n- мойку бутылок \nЭта группировка не включает: \n- пароструйную, пескоструйную и прочую  очистку  фасадов зданий, см. 45.45 \n- чистку ковров, драпировок, занавесей и штор, см. 93.01', 1842, '74.70', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1844, '74.70.2', 'Чистка и уборка транспортных средств', 'Чистка и уборка транспортных средств \nЭта группировка включает: \n- уборку поездов, автобусов, самолетов и т.п. \n- очистку изнутри автоцистерн и танкеров', 1842, '74.70', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1845, '74.70.3', 'Деятельность по проведению дезинфекционных, дезинсекционных и дератизационных работ', 'Деятельность по        проведению       дезинфекционных, дезинсекционных и дератизационных работ \nЭта группировка включает: \n- предоставление  услуг  по  дезинфекции,  дезинсекции и дератизации  зданий  и  транспортных   средств   (судов, поездов и т.п.) \nЭта группировка не включает: \n- защиту  растений от болезней и насекомых \n- вредителей, см. 01.41.1', 1842, '74.70', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1846, '74.8', 'Предоставление различных видов услуг', 'Предоставление различных видов услуг', 1777, '74', 5);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1847, '74.81', 'Деятельность в области фотографии', 'Деятельность в области фотографии \nЭта группировка включает: \n- деятельность в  области  фотографии  для  коммерческих целей и частных клиентов: \n- фотосъемку, включая аэрофотосъемку \n- изготовление  фотографий:  портретных  фотографий   на документы,  школьных  и  свадебных  фотографий  и  т.п.; фотографий   для   рекламы,    издательской   продукции, журналов  мод,  операций  с  недвижимостью,  для   целей туризма, аэрофотоснимков \n- обработку    фотопленок:   проявление,   печатание   и увеличение  с   фотографий,  негативов  или  кинопленок, снятых  клиентами;   помещение   диапозитивов  в  рамки; пересъемку, восстановление или ретуширование фотографий \nЭта группировка не включает: \n- аэрофотосъемку для исследовательских и  изыскательских целей, см. 74.20.34 \n- киносъемки  и   обработку   кинопленок,  связанных   с кинопроизводством и телевидением, см. 92.11', 1846, '74.8', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1848, '74.82', 'Упаковывание', 'Упаковывание \nЭта группировка включает: \n- деятельность по  упаковыванию  за  вознаграждение  или на    договорной    основе,    независимо    от    того, автоматизирован или не автоматизирован этот процесс: \n- фасование и упаковывание мяса \n- заполнение  бутылок   и   банок   жидкими  продуктами, включая напитки и пищевые продукты \n- упаковывание    твердых    товаров     в    прозрачную (блистерную) упаковку, в фольгу и т.п. \n- этикетирование,  нанесение  маркировки и  надписей  на упаковку \n- упаковывание в пакеты и упаковывание подарков \nЭта группировка не включает: \n- упаковывание   медицинской    продукции,   см.   24.4, 24.66.4, 33.1 \n- упаковывание, связанное с перевозкой, см. 63.40', 1846, '74.8', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1849, '74.85', 'Предоставление секретарских, редакторских услуг и услуг по переводу', 'Предоставление секретарских, редакторских услуг и  услуг по переводу \nЭта группировка включает: \n- деятельность в области  стенографирования  и  почтовых отправлений: \n- печатание и считывание подготовленного текста \n- прочую   секретарскую    деятельность,    такую    как расшифровка звукозаписей с магнитных лент или дисков \n- копирование,    светокопирование      и    аналогичную деятельность \n- нанесение  адресов   на   конверты,  раскладывание  по конвертам, запечатывание и  отправку  корреспонденции  и т.п., включая отправку рекламных материалов \n- письменный и устный  перевод,  включая  предоставление услуг  по   письменному   переводу,  выполненному  путем доработки автоматического перевода \nЭта группировка также включает: \n- чтение корректур \nЭта группировка не включает: \n- предоставление  услуг   по  автоматическому  переводу, см. 72.30 \n- деятельность, связанную с базами данных, см. 72.40 \n- деятельность  в   области  бухгалтерского  учета,  см. 74.12', 1846, '74.8', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1850, '74.86', 'Деятельность центров телефонного обслуживания', 'Деятельность центров телефонного обслуживания \nЭта группировка включает: \n- техническое  обеспечение   связи    и     обслуживание заказчика  за   счет  другого  лица  центрами  обработки телефонных   вызовов,    объединенными    в     сеть   и осуществляющими    обработку       телефонных    вызовов пользователей с  использованием  автоматической  системы перераспределения вызовов,  компьютерной  телефонии  или системы интерактивного речевого взаимодействия: \n- предоставление услуг по размещению заказов \n- предоставление информации о товарах \n- учет претензий и рекламаций \n- техническое обеспечение связи и обслуживание заказчика за   счет   другого  лица  центрами обработки телефонных вызовов,  не  объединенными  в  сеть  и  осуществляющими предоставление услуг, связанных со сбытом и  маркетингом по отношению к конкретному клиенту: \n- по изучению рынка \n- прямого маркетинга \n- проверки адреса', 1846, '74.8', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1851, '74.87', 'Предоставление прочих деловых услуг, не включенных в другие группировки', 'Предоставление прочих деловых  услуг,  не  включенных  в другие группировки', 1846, '74.8', 8);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1852, '74.87.1', 'Взыскание платежей по счетам, оценка платежеспособности в связи с финансовым состоянием или коммерческой практикой частного лица или фирмы', 'Взыскание платежей по счетам, оценка  платежеспособности в  связи  с  финансовым   состоянием   или  коммерческой практикой частного лица или фирмы', 1851, '74.87', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1853, '74.87.2', 'Предоставление посреднических услуг по организации покупки и продажи мелких или средних коммерческих предприятий, включая профессиональную практику', 'Предоставление  посреднических   услуг   по  организации покупки и  продажи  мелких   или   средних  коммерческих предприятий, включая профессиональную практику', 1851, '74.87', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1854, '74.87.3', 'Деятельность по оценке стоимости, кроме оценки, связанной с недвижимым имуществом или страхованием', 'Деятельность  по   оценке   стоимости,   кроме   оценки, связанной с недвижимым имуществом или страхованием', 1851, '74.87', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1855, '74.87.4', 'Деятельность в области дизайна', 'Деятельность в области дизайна \nЭта группировка включает: \n- предоставление услуг графического дизайна \n- предоставление услуг средового, ландшафтного  дизайна, дизайна интерьера \n- моделирование  текстильных   изделий,  одежды,  обуви, ювелирных изделий, мебели, других  предметов  интерьера, модных  товаров,  а  также  прочих  товаров  личного   и домашнего пользования \n- деятельность дизайнеров выставочных стендов \nЭта группировка не включает: \n- деятельность в области  промышленного  и транспортного дизайна, см. 74.20.16', 1851, '74.87', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1856, '74.87.5', 'Предоставление услуг по оформлению помещений, деятельность по организации ярмарок, выставок и конгрессов', 'Предоставление   услуг     по    оформлению   помещений, деятельность  по   организации   ярмарок,   выставок   и конгрессов', 1851, '74.87', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1857, '74.87.6', 'Деятельность самостоятельных аукционистов', 'Деятельность самостоятельных аукционистов', 1851, '74.87', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1858, '74.87.7', 'Деятельность консультантов (кроме консультантов по инженерному проектированию), не включенная в другие группировки', 'Деятельность  консультантов   (кроме   консультантов  по инженерному  проектированию),  не  включенная  в  другие группировки', 1851, '74.87', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1859, '74.87.8', 'Предоставление прочих деловых услуг', 'Предоставление прочих деловых услуг \nЭта группировка включает: \n- деятельность  по  выпуску и  распространению  купонов, дающих  право  на  скидку  или  бесплатное  приобретение товара \n- считывание   показаний   счетчиков    газа,   воды   и электричества \n- деятельность агентов  и  агентств,  осуществляемую  от имени отдельных лиц и  обычно  связанную  с  заключением контрактов   (договоров)   на   участие  в  кинофильмах, театральных постановках  и  других  развлекательных  или спортивных мероприятиях, а также  с  предложением  книг, пьес, предметов изобразительного  искусства,  фотографий и т.п. издателям, продюсерам и т.п. \nЭта группировка не включает: \n- проектирование   производственных  помещений,  включая размещение машин и оборудования, см. 74.20.12 \n- деятельность по размещению рекламы и прочих  рекламных средств, см. 74.40', 1851, '74.87', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1860, 'РАЗДЕЛ L', 'ГОСУДАРСТВЕННОЕ УПРАВЛЕНИЕ И ОБЕСПЕЧЕНИЕ ВОЕННОЙ БЕЗОПАСНОСТИ; СОЦИАЛЬНОЕ СТРАХОВАНИЕ', 'БЕЗОПАСНОСТИ; СОЦИАЛЬНОЕ СТРАХОВАНИЕ Виды  деятельности,  классифицированные  в  любых других группировках,  не  включаются  в класс 75, даже если эта деятельность       осуществляется       государственными учреждениями. Например управление системой образования в школе  (т.е.  составление  правил  распорядка,  программ обучения,  осуществление  проверок) попадает в класс 75, но сам процесс обучения не относится к этому классу, см. 80,  тюремные  и  военные  госпитали  классифицируются в сфере  здравоохранения, см. 85, а удаление сточных вод и отходов   классифицируется  в  классе  90.  Этот  раздел включает     деятельность,     обычно     осуществляемую государственными     учреждениями.    Юридический    или институциональный статус сам по себе не является при при этом   определяющим   фактором.   Этот  раздел  включает учреждения,   которые   являются   частью   местных  или центральных   органов   власти  и  которые  осуществляют управление  в  определенном аспекте. Таким образом, этот раздел включает: \n-   обеспечение   безопасности,   правосудие,   полицию, международную деятельность и т.д.; \n-  управление  общего  характера  (т.е. исполнительную и законодательную  власть,  управление финансами и т.д. на всех уровнях государственного управления); \n-      контроль     и     надзор     за     учреждениями социально-экономической сферы; \n-   управление   в   сфере   обязательного   социального страхования', NULL, NULL, 1);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1861, 'Подраздел LA', 'ГОСУДАРСТВЕННОЕ УПРАВЛЕНИЕ И ОБЕСПЕЧЕНИЕ ВОЕННОЙ БЕЗОПАСНОСТИ; СОЦИАЛЬНОЕ СТРАХОВАНИЕ', 'БЕЗОПАСНОСТИ; СОЦИАЛЬНОЕ СТРАХОВАНИЕ', 1860, 'РАЗДЕЛ L', 1);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1862, '75', 'Государственное управление и обеспечение военной безопасности; социальное страхование', 'Государственное   управление   и    обеспечение  военной безопасности; социальное страхование Эта   группировка    включает    деятельность,    обычно осуществляемую         государственными    учреждениями. Юридический или институциональный статус  сам   по  себе не является при этом определяющим фактором.  Группировка включает учреждения, которые являются частью местных или центральных   органов   власти  и  которые  осуществляют управление  определенным   сообществом   в  определенном аспекте. Виды    деятельности,     классифицированные    в  любых других группировках, не попадают в эту группировку, даже если  эта деятельность  осуществляется  государственными учреждениями. Например, управление системой  образования в  школе  (т.е. составление правил распорядка,  программ обучения, осуществление проверок) попадает в группировку 75, но сам процесс обучения не относится к этому разделу (см. 80 "Образование"),  тюремные  и  военные  госпитали классифицируются   в   сфере   здравоохранения  (см.  85 "Здравоохранение и предоставление социальных услуг"),  а удаление  сточных  вод  и  отходов  \n- в разделе 90 "Сбор сточных вод, отходов и аналогичная деятельность"', 1861, 'Подраздел LA', 3);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1863, '75.1', 'Государственное управление общего и социально экономического характера', 'Государственное управление   общего   и   социально    \n- экономического характера', 1862, '75', 4);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1864, '75.11', 'Государственное управление общего характера', 'Государственное управление общего характера \nЭта группировка включает: \n- законодательную    и    исполнительную    деятельность центральных, региональных и местных органов управления \n- управление и надзор за налоговой деятельностью: \n- управление системами налогообложения \n- сбор  налогов  и  пошлин  на  товары  и  расследование случаев нарушения налогового законодательства \n- управление таможней \n- исполнение  бюджета   и   управление  государственными фондами  и   государственным  долгом:  сбор  налогов   и получение денежных средств, контроль за их расходованием \n- управление   и   функционирование   служб   социально\n- экономического   планирования,     статистических      и социологических служб на разных уровнях государственного управления \n- управление,  государственный   контроль  и  надзор  за деятельностью  в  области  природопользования  и  охраны окружающей среды: \n- государственный   контроль    и    надзор    в   сфере природопользования \n- государственный  контроль  и  надзор  в  сфере  охраны окружающей    среды    (государственный    экологический контроль) \n- управление  и   надзор   в   области   государственной экологической экспертизы \n- управление в области  организации  и  функционирования особо охраняемых природных территорий \n- разрешительная     деятельность       в        области природопользования и охраны окружающей среды', 1863, '75.1', 8);
