INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1879, '75.11.4', 'Управление финансовой и фискальной деятельностью', 'Управление финансовой и фискальной деятельностью \nЭта группировка включает: \n- общий контроль над доходами \n- управление   и   надзор  за  налоговой  деятельностью: управление системами  налогообложения;  сбор  налогов  и пошлин  на  товары  и  расследование  случаев  нарушения налогового законодательства \n- управление таможней \n- исполнение  бюджета  и   управление   государственными фондами   и   государственным  долгом:  сбор  налогов  и получение денежных средств, контроль за их расходованием', 1864, '75.11', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1880, '75.11.5', 'Управление деятельностью в области прогнозирования и планирования', 'Управление деятельностью  в  области  прогнозирования  и планирования \nЭта группировка включает: \n- управление и функционирование служб прогнозирования  и социально   \n-   экономического  планирования  на  разных уровнях государственного управления', 1864, '75.11', 0);
