INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1906, '75.23.32', 'Деятельность прокуратур субъектов Российской Федерации', 'Деятельность прокуратур субъектов Российской Федерации', 1904, '75.23.3', 0);
