INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1922, 'Подраздел MA', 'ОБРАЗОВАНИЕ', '', 1921, 'РАЗДЕЛ M', 1);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1923, '80', 'Образование', 'Образование \nЭта группировка включает: \n- виды  деятельности,  связанные   с   государственными, муниципальными,       негосударственными      (частными) образовательными     учреждениями        всех     видов. Образовательные   программы   осваиваются  в   следующих формах: \n- в  общеобразовательных  учреждениях  в   очной,  очно\n- заочной (вечерней), заочной формах \n- в  форме   семейного   образования,   самообразования, экстерната. Допускается   сочетание    различных    форм   получения образования. Каждый     уровень  образования   включает  деятельность специальных (коррекционных)  образовательных  учреждений (классов, групп), обеспечивающих лечение,  воспитание  и обучение, социальную адаптацию и интеграцию  в  общество детей  и   подростков   с   физическими   и  умственными недостатками. В данной группировке  классифицируется  образование  для взрослых,  по  содержанию  соответствующее  определенным уровням общего образования \nЭта группировка также включает: \n- прочие виды образования и обучения, например  обучение в школах водителей транспортных средств \nЭта группировка не включает: \n- обучение,  направленное   на   организацию  отдыха   и развлечений,  например  обучение  спортивным  играм, см. 92.6, 92.7', 1922, 'Подраздел MA', 4);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1924, '80.1', 'Дошкольное и начальное общее образование', 'Дошкольное и начальное общее образование Обучение может  осуществляться  на  занятиях  в  школах, посредством радио или телевидения, через  сеть  Интернет или как заочное обучение', 1923, '80', 1);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1925, '80.10', 'Дошкольное и начальное общее образование', 'Дошкольное и начальное общее образование Обучение может  осуществляться  на  занятиях  в  школах, посредством радио или  телевидения, через сеть  Интернет или как заочное обучение', 1924, '80.1', 3);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1926, '80.10.1', 'Дошкольное образование (предшествующее начальному общему образованию)', 'Дошкольное   образование   (предшествующее    начальному общему образованию) \nЭта группировка включает: \n- деятельность   сети   дошкольных   общеобразовательных учреждений,  реализующих  общеобразовательные  программы дошкольного   образования    различной   направленности, обеспечивающих  воспитание  и  обучение  детей  (детские сады, подготовительные классы и т.п.). Обучение может  осуществляться  на  занятиях  в  школах, посредством радио или телевидения, через  сеть  Интернет или как заочное обучение \nЭта группировка не включает: \n- деятельность по уходу за детьми в дневное  время,  см. 85.32 \n- деятельность детских домов, интернатов для детей,  см. 85.31', 1925, '80.10', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1927, '80.10.2', 'Начальное общее образование', 'Начальное общее образование \nЭта группировка включает: \n- начальное       общее       школьное      образование, предусматривающее  обучение   учащихся  чтению,  письму, счету,   основным    умениям     и    навыкам    учебной деятельности,   элементам    теоретического    мышления, простейшим  навыкам   самоконтроля   учебных   действий, культуре поведения и  речи,  основам  личной  гигиены  и здорового образа  жизни  в  образовательных  учреждениях для детей дошкольного и младшего школьного возраста \nЭта группировка не включает: \n- начальное профессиональное образование, см. 80.22.1', 1925, '80.10', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1928, '80.10.3', 'Дополнительное образование детей', 'Дополнительное образование детей \nЭта группировка включает: \n- дополнительное   образование   для  детей  в  возрасте преимущественно от  6  до  18  лет,  основными  задачами которого  является  обеспечение  необходимых условий для личностного     развития,      укрепления      здоровья, профессионального  самоопределения  и  творческого труда детей, осуществляемое: \n- во   внешкольных   учреждениях  (детских  музыкальных школах,  художественных школах,  школах искусств, домах детского творчества и др.) \n- в общеобразовательных  учреждениях  и образовательных учреждениях профессионального образования', 1925, '80.10', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1929, '80.2', 'Основное общее, среднее (полное) общее, начальное и среднее профессиональное образование', 'Основное  общее,   среднее  (полное)   общее,  начальное профессиональное образование и среднее  профессиональное образование Обучение может  осуществляться  на  занятиях  в  школах, посредством радио или телевидения, через  сеть  Интернет или как заочное обучение', 1923, '80', 2);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1930, '80.21', 'Основное общее и среднее (полное) общее образование', 'Основное общее и среднее (полное) общее образование \nЭта группировка не включает: \n- образование для взрослых, см. 80.42', 1929, '80.2', 2);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1931, '80.21.1', 'Основное общее образование', 'Основное общее образование \nЭта группировка включает: \n- основное общее школьное образование,  являющееся базой для получения  среднего  (полного)  общего  образования, начального  и  среднего  профессионального  образования, связанное   с   обязательным   посещением   занятий    в общеобразовательных    учреждениях   (школе,   школе   \n- интернате, лицее, гимназии и др.) \n- основное   общее   школьное   образование,  получаемое одновременно с начальным  профессиональным  образованием (средние музыкальные,  хореографические,  художественные школы, школы искусств и т.п.)', 1930, '80.21', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1932, '80.21.2', 'Среднее (полное) общее образование', 'Среднее (полное) общее образование \nЭта группировка включает: \n- среднее   (полное)  общее  образование,  получаемое  в общеобразовательных   учреждениях   (школе,   школе    \n- интернате,  лицее, гимназии и др.), являющееся базой для среднего профессионального и  высшего  профессионального образования \n- среднее   (полное)   общее   образование,   получаемое одновременно  с  начальным профессиональным образованием (средние музыкальные,  хореографические,  художественные школы, школы искусств и т.п.)', 1930, '80.21', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1933, '80.22', 'Начальное и среднее профессиональное образование', 'Начальное и среднее профессиональное образование \nЭта группировка не включает: \n- профессиональное   образование   на   уровне   высшего учебного заведения, см. 80.30', 1929, '80.2', 2);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1934, '80.22.1', 'Начальное профессиональное образование', 'Начальное профессиональное образование \nЭта группировка включает: \n- начальное профессиональное образование,  получаемое  в профессиональных  училищах,  лицеях,   учебно \n- курсовых комбинатах и других образовательных учреждениях  данного уровня', 1933, '80.22', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1935, '80.22.2', 'Среднее профессиональное образование', 'Среднее профессиональное образование', 1933, '80.22', 3);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1936, '80.22.21', 'Обучение в образовательных учреждениях среднего профессионального образования', 'Обучение в    образовательных    учреждениях    среднего профессионального образования \nЭта группировка включает: \n- техническое  и  другое  профессиональное   образование более   низкой   ступени,  чем  высшее  профессиональное образование,  получаемое, как правило, в образовательных учреждениях   среднего   профессионального   образования (техникумах,  колледжах,  техникумах  \n-  предприятиях  и др.).  Среднее  профессиональное образование имеет целью подготовку специалистов среднего звена на базе основного общего,   среднего   (полного)   общего  или  начального профессионального образования. Как правило, в программах придается  особое  значение  предметной  специализации и обучению как теоретическим основам,  так и  практическим навыкам,  обычно связанным с настоящим или будущим родом занятий', 1935, '80.22.2', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1937, '80.22.22', 'Обучение в образовательных учреждениях дополнительного профессионального образования (повышения квалификации) для специалистов, имеющих среднее профессиональное образование', 'Обучение в  образовательных  учреждениях дополнительного профессионального образования  (повышения  квалификации) для   специалистов,   имеющих  среднее  профессиональное образование \nЭта группировка включает: \n- дополнительное образование (переподготовку,  повышение квалификации  и  др.) для специалистов,  имеющих среднее профессиональное образование,  получаемое  в  институтах повышения    квалификации,    на   курсах,   в   центрах профессиональной    ориентации,    в     образовательных учреждениях   профессионального   образования  и  других образовательных  учреждениях  в   связи   с   постоянным совершенствованием образовательных стандартов', 1935, '80.22.2', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1938, '80.22.23', 'Обучение на подготовительных курсах для поступления в образовательные учреждения среднего профессионального образования', 'Обучение на подготовительных курсах  для  поступления  в образовательные  учреждения  среднего  профессионального образования', 1935, '80.22.2', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1939, '80.3', 'Высшее профессиональное образование', 'Высшее профессиональное образование Обучение может  осуществляться  на  занятиях  в  учебных заведениях,  посредством  радио  или  телевидения, через сеть Интернет или как заочное обучение', 1923, '80', 1);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1940, '80.30', 'Высшее профессиональное образование', 'Высшее профессиональное образование', 1939, '80.3', 4);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1941, '80.30.1', 'Обучение в образовательных учреждениях высшего профессионального образования (университетах, академиях, институтах и в др.)', 'Обучение в     образовательных    учреждениях    высшего профессионального образования (университетах, академиях, институтах и в др.) \nЭта группировка включает: \n- образование,      обеспечивающее      подготовку     и переподготовку  специалистов  соответствующего   уровня, удовлетворяющее  потребности  личности  в  углублении  и расширении  образования  на  базе   среднего   (полного) общего,   среднего   профессионального   образования   в образовательных  учреждениях  высшего  профессионального образования (высших учебных заведениях):  университетах, академиях,   институтах.    В    Российской    Федерации устанавливаются      следующие      ступени      высшего профессионального      образования,       подтверждаемые присвоением      лицу      квалификации:     "бакалавр", "дипломированный специалист", "магистр"', 1940, '80.30', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1942, '80.30.2', 'Послевузовское профессиональное образование', 'Послевузовское профессиональное образование \nЭта группировка включает: \n- послевузовское  профессиональное образование,  которое предоставляет возможность повышения уровня  образования, научной,  педагогической  квалификации  на  базе высшего профессионального образования (аспирантуры,  ординатуры, адъюнктуры и т.п.)', 1940, '80.30', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1943, '80.30.3', 'Обучение в образовательных учреждениях дополнительного профессионального образования (повышения квалификации) для специалистов, имеющих высшее профессиональное образование', 'Обучение в образовательных  учреждениях  дополнительного профессионального  образования  (повышения квалификации) для  специалистов,   имеющих   высшее   профессиональное образование \nЭта группировка включает: \n- дополнительное  образование для специалистов,  имеющих высшее  профессиональное   образование,   получаемое   в институтах   повышения   квалификации,   высших  учебных заведениях,  на  курсах,  в   центрах   профессиональной ориентации и др. в связи с постоянным совершенствованием образовательных стандартов', 1940, '80.30', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1944, '80.30.4', 'Обучение на подготовительных курсах для поступления в учебные заведения высшего профессионального образования', 'Обучение на  подготовительных  курсах  для поступления в учебные заведения высшего профессионального образования', 1940, '80.30', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1945, '80.4', 'Образование для взрослых и прочие виды образования', 'Образование для взрослых и прочие виды образования \nЭта группировка  включает  специальное  образование  для взрослых,  не   относящееся    к   общему   образованию, классифицированному в группировках 80.1 \n- 80.3. Обучение может  осуществляться  на  занятиях  в  учебных заведениях, посредством  радио  или  телевидения,  через сеть Интернет или как заочное обучение', 1923, '80', 2);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1946, '80.41', 'Обучение водителей транспортных средств', 'Обучение водителей транспортных средств \nЭта группировка включает: \n- обучение  для  получения  водительских  удостоверений, летных сертификатов, навигационных лицензий и т.п.', 1945, '80.4', 2);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1947, '80.41.1', 'Обучение водителей автотранспортных средств', 'Обучение водителей автотранспортных средств', 1946, '80.41', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1948, '80.41.2', 'Обучение летного и мореходного персонала', 'Обучение летного и мореходного персонала', 1946, '80.41', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1949, '80.42', 'Образование для взрослых и прочие виды образования, не включенные в другие группировки', 'Образование для взрослых и прочие виды  образования,  не включенные в другие группировки \nЭта группировка включает: \n- образование  для  взрослых,  которые  не  обучаются  в системе  регулярного   общего  образования  или  высшего профессионального    образования.     Обучение     может проводиться  на  дневных  или  на  вечерних  занятиях  в школах  или   в   специальных   учебных  заведениях  для взрослых. В программы обучения  могут  включаться  такие специальные  предметы,   как,   например,   компьютерное образование для взрослых \n- дополнительное  образование   в   целях  всестороннего удовлетворения   образовательных  потребностей  граждан, общества,       государства,      осуществляемое       в образовательных        учреждениях       дополнительного образования,   а    также   посредством   индивидуальной педагогической деятельности \nЭта группировка не включает: \n- высшее профессиональное образование, см. 80.30 \n- деятельность школ танцев, см. 92.34.2 \n- тренировочную деятельность в области спорта и игр, см. 92.62', 1945, '80.4', 0);
