INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1974, 'Подраздел OA', 'ПРЕДОСТАВЛЕНИЕ ПРОЧИХ КОММУНАЛЬНЫХ, СОЦИАЛЬНЫХ И ПЕРСОНАЛЬНЫХ УСЛУГ', 'ПЕРСОНАЛЬНЫХ УСЛУГ', 1973, 'РАЗДЕЛ O', 4);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1975, '90', 'Сбор сточных вод, отходов и аналогичная деятельность', 'Сбор сточных вод, отходов и аналогичная деятельность \nЭта группировка включает: \n- сбор и обработку  отходов  производства и  потребления не   для    дальнейшего    использования    в   процессе промышленной   переработки,   а   с   целью  удаления  и выявления более или менее ценных продуктов. \nЭта группировка также включает: \n- прочие   виды   деятельности,    такие    как   уборка территории, очистка от снега и т.п. \nЭта группировка не включает: \n- обработку отходов, лома и других  материалов  с  целью получения  вторичного  сырьевого  материала,  пригодного для   непосредственного    использования    в   процессе промышленной переработки, а не  как конечного  продукта, см. 37.10 и 37.20 \n- оптовую  торговлю  (покупку   и  продажу)  отходами  и ломом,   включая   сбор,   сортирование,   упаковывание, распределение и т.п., но без  существенной  переработки, см. 51.57', 1974, 'Подраздел OA', 1);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1976, '90.0', 'Сбор сточных вод, отходов и аналогичная деятельность', 'Сбор сточных вод, отходов и аналогичная деятельность', 1975, '90', 3);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1977, '90.01', 'Сбор и обработка сточных вод', 'Сбор и обработка сточных вод \nЭта группировка включает: \n- сбор  и   отвод  бытовых  сточных  вод  (в  том  числе индивидуальных пользователей), а  также  дождевой   воды через  канализационные   сети,   коллекторы,   перевозку цистернами  и  другими   транспортными   средствами  для перевозки отходов, их обработку и удаление \n- очистку  сточных   вод  с  использованием  физических, химических   и   биологических   методов,    таких   как разжижение,   фильтрование,   седиментация,  и   другими способами \n- техническое  обслуживание   и  чистку  канализационных систем \n- опорожнение  и  чистку  выгребных   ям, отстойников  и септиков,  сточных  колодцев,  обслуживание  туалетов  с химической стерилизацией \n- обработку сточных вод промышленных предприятий \n- обработку сточных вод плавательных бассейнов \nЭта группировка не включает: \n- строительство канализационных систем, см. 45.21 \n- устранение   повреждений  канализационных  систем, см. 45.33 \n- обработку  загрязненной почвы  и  поверхности  воды  в сочетании  с  очисткой  загрязненной  окружающей  среды, см. 90.03', 1976, '90.0', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1978, '90.02', 'Сбор и обработка прочих отходов', 'Сбор и обработка прочих отходов \nЭта группировка включает: \n- сбор отходов производства  и  потребления  в  мусорные баки, баки на колесах, контейнеры и т.п. \n- сбор  опасных   отходов,   использованных    батареек, использованных кулинарных масел и жиров \n- сбор отработанных масел  на  водном  транспорте  и  из гаражей \n- сбор строительных отходов и отходов после разрушений \n- эксплуатацию мусорных полигонов \n- удаление  отходов   методом   сжигания   или   другими способами: \n- размещение  отходов   на    земле,   захоронение   или запахивание отходов \n- обработку отходов  на  мусороперерабатывающих  заводах с  целью   уничтожения   или   получения  промежуточного продукта (компоста) \n- обработку и уничтожение зараженных животных  и  трупов животных и других токсичных отходов \n- обеззараживание медицинских отходов и их уничтожение \n- обработку и удаление  радиоактивных  отходов  (главным образом медицинского происхождения), которые  подвержены радиоактивному  распаду  в  течение  периода  временного хранения,  после   которого   перестают   подпадать  под действие Федеральных норм и правил в области  ядерной  и радиационной    безопасности,      или    низкоактивного загрязненного грунта \n- уничтожение   использованных    товаров,      например холодильников, с целью устранения вредных отходов \nЭта группировка не включает: \n- переработку отходов продуктов для производства пищевых продуктов, см. 15 \n- переработку отходов продуктов с бойни для производства кормов для животных, см. 15.7 \n- регенерацию ядерного топлива и обработку радиоактивных ядерных отходов, см. 23.30 \n- производство компоста, см. 24.15 \n- переработку  отходов    пищевых   продуктов,   включая напитки, и табачных  изделий  во  вторичное  сырье,  см. 37.20 \n- обработку отходов и  лома  без  процесса  значительных третьим лицам, такую  как  демонтаж  автомобилей,  машин или  компьютеров,   или   такую   как  сортирование  или прессование  отходов    бумаги,    тканей,   пластмассы, древесины и т.п., см. 50, 51, 52 \n- сбор отходов  как  часть  оптовой  торговли  отходами, см. 51.57', 1976, '90.0', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1979, '90.03', 'Уборка территории, восстановление после загрязнения и аналогичная деятельность', 'Уборка территории, восстановление  после  загрязнения  и аналогичная деятельность \nЭта группировка включает: \n- обеззараживание  почвы   и   грунтовых  вод  в  местах загрязнения, на месте или не на месте, с  использованием механических,   физико-химических    или   биологических методов \n- обеззараживание  и   очистку  поверхности  воды  после случайного загрязнения, например  сбор загрязнителя  или применение химических веществ \n- очистку после разлива нефти на земле,  на  поверхности воды, в океанах и морях, включая прибрежные воды \n- сбор мусора из мусорных урн в общественных местах \n- подметание и поливку  улиц,  скверов,  дорог,  рынков, общественных садов и парков и т.п. \n- очистку от  снега  и  льда   улиц,   дорог,   взлетно\n- посадочных  полос   на  аэродромах,   включая  обработку реагентами, в том числе солью, песком и т.п. \n- зачистку (уборку породы из  забоя)  шахт,  рудников  и т.п. (включая после взрывов) \n- прочую деятельность  по  специализированному  контролю за загрязнениями, не включенную в другие группировки \n- аренду туалетных кабинок \nЭта группировка не включает: \n- предоставление услуг по защите  растений  от  болезней и вредителей, см. 01.41.1 \n- предоставление  услуг  по  эксплуатации  мелиоративных систем, см. 01.41.3 \n- очистку грунтовых вод  для  целей  водоснабжения,  см. 41.00 \n- работы по удалению загрязненного верхнего  слоя  почвы (грунта) как часть деятельности в области строительства, см. 45.11 \n- уплотнение почвы, см. 45.11 \n- работы по удалению асбеста, см. 45.25 \n- перевозку загрязненной почвы,  уже  удаленной  третьей стороной, см. 60.24 \n- испытания и  анализ состава  и  чистоты  материалов  и веществ, см. 74.30.1 \n- деятельность по  проведению  дезинфекции,  дезинсекции и дератизации в помещениях, 74.70 \n- деятельность по отводу сточных вод, см. 90.01', 1976, '90.0', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1980, '91', 'Деятельность общественных объединений', 'Деятельность общественных объединений', 1974, 'Подраздел OA', 3);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1981, '91.1', 'Деятельность коммерческих, предпринимательских и профессиональных организаций', 'Деятельность коммерческих,     предпринимательских     и профессиональных организаций', 1980, '91', 2);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1982, '91.11', 'Деятельность коммерческих и предпринимательских организаций', 'Деятельность коммерческих      и     предпринимательских организаций \nЭта группировка включает: \n- деятельность  организаций,  интересы  членов   которых сосредоточены  на  обеспечении  развития  и  процветания предприятий  конкретной  сферы  деятельности,   например торговли,    сельского   хозяйства   и   др.,   или   на экономическом  развитии   и   экономической   обстановке конкретного  географического района или административной единицы   независимо   от    направления    коммерческой деятельности.   Основные   виды  предоставляемых  услуг: распространение    информации,    представительство    в государственных  учреждениях,  связи с общественностью и переговоры с профсоюзами. Включена деятельность торговых палат, союзов, гильдий и аналогичных организаций', 1981, '91.1', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1983, '91.12', 'Деятельность профессиональных организаций', 'Деятельность профессиональных организаций \nЭта группировка включает: \n- деятельность  организаций,  интересы  членов   которых сосредоточены  на  конкретной отрасли знаний или отрасли профессиональной    практической    деятельности     или технической    области.    Сюда   относятся   ассоциации специалистов, занимающихся деятельностью в области науки и культуры (писателей,  художников, актеров, журналистов и т.п.).  Основные виды предоставляемых  услуг  включают распространение   информации,   установление   критериев оценки  практической  деятельности  и  контроль  за   их соблюдением,    представительство    в   государственных учреждениях  и  связи  с   общественностью.   Включается деятельность научных обществ', 1981, '91.1', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1984, '91.2', 'Деятельность профессиональных союзов', 'Деятельность профессиональных союзов', 1980, '91', 1);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1985, '91.20', 'Деятельность профессиональных союзов', 'Деятельность профессиональных союзов \nЭта группировка включает: \n- деятельность ассоциаций, членами которых являются лица наемного труда,  заинтересованные,  главным  образом,  в представлении  своих  интересов  по  вопросам заработной платы и условий труда, а также в согласованных действиях через   организацию.   Включается   деятельность  союзов работников  одного  предприятия,  союзов,   объединяющих смежные    отрасли,    а    также   союзов   работников, объединяющихся  по   профессиональному,   региональному, структурному и другим признакам', 1984, '91.2', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1986, '91.3', 'Деятельность прочих общественных объединений', 'Деятельность прочих общественных объединений', 1980, '91', 3);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1987, '91.31', 'Деятельность религиозных организаций', 'Деятельность религиозных организаций \nЭта группировка включает: \n- деятельность религиозных организаций \n- деятельность мужских и женских монастырей \nЭта группировка не включает: \n- деятельность  в  области  образования,  осуществляемую этими организациями, см. 80 \n- деятельность в области здравоохранения, осуществляемую этими организациями, см. 85.1 \n- предоставление социальных услуг,  осуществляемое этими организациями, см. 85.3', 1986, '91.3', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1988, '91.32', 'Деятельность политических организаций', 'Деятельность политических организаций \nЭта группировка включает: \n- деятельность      политических      организаций      и вспомогательных   организаций   (организаций   молодежи, взаимодействующих  с  политической  партией,   и   др.). Деятельность   таких   организаций  направлена,  главным образом,  на продвижение членов партии  и  сочувствующих партии  лиц  на  политические  и  другие  посты  с целью оказания  влияния  на  решения,   принимаемые   органами власти,   а   также   на   распространение   информации, организацию связи с общественностью, сбор средств и т.п.', 1986, '91.3', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(1989, '91.33', 'Деятельность прочих общественных организаций, не включенных в другие группировки', 'Деятельность   прочих   общественных   организаций,   не включенных в другие группировки \nЭта группировка включает: \n- деятельность организаций, не связанных непосредственно с   политическими    партиями,   способствующих  решению общественных задач и проблем  посредством  общественного образования,  политического  влияния,  сбора  средств  и т.п.: \n- гражданских инициатив и движений протеста \n- движений в области защиты окружающей среды и экологии \n- выступающих в  поддержку  обществ  и  образования,  не включенных в другие группировки \n- выступающих в защиту и за улучшение  положения  особых групп населения, например этнических групп и меньшинств \n- имеющих  патриотические   цели,   включая  объединения ветеранов войны \n- деятельность  групп,  объединенных  общими  интересами (туристских клубов, объединений автомобилистов,  обществ потребителей и др.) \n- деятельность   обществ,    целью    которых   является установление социальных контактов (клубов деловых людей, масонских лож и др.) \n- деятельность ассоциаций молодежи, детских и  юношеских организаций,  студенческих  организаций, клубов, обществ и т.п. \n- деятельность   ассоциаций,    создаваемых    с   целью проведения культурных и  развлекательных  мероприятий  и организации  досуга,  кроме  спорта  или  игр,  например поэтических,  литературных  клубов,   обществ  любителей истории, садоводства, клубов  кино\n-    и  фотолюбителей, клубов любителей музыки,  живописи,  любителей  ремесел, коллекционеров и т.п. \n- деятельность обществ защиты животных \nЭта группировка не включает: \n- деятельность профессиональных организаций, см. 91.12 \n- организации,  осуществляющие  деятельность  в  области искусства и содействующие этой деятельности, см. 92.31', 1986, '91.3', 0);
