INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(2001, '92.31.21', 'Деятельность по организации и постановке театральных и оперных представлений, концертов и прочих сценических выступлений', 'Деятельность по организации и постановке  театральных  и оперных  представлений,  концертов  и прочих сценических выступлений \nЭта группировка включает: \n- деятельность ансамблей, театральных трупп, оркестров и групп музыкантов', 2000, '92.31.2', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(2002, '92.31.22', 'Деятельность актеров, режиссеров, композиторов, художников, скульпторов и прочих представителей творческих профессий, выступающих на индивидуальной основе', 'Деятельность актеров,     режиссеров,      композиторов, художников,    скульпторов   и   прочих   представителей творческих  профессий,  выступающих  на   индивидуальной основе', 2000, '92.31.2', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(2003, '92.32', 'Деятельность концертных и театральных залов', 'Деятельность концертных и театральных залов \nЭта группировка включает: \n- деятельность концертных и театральных залов  и  других учреждений культуры \n- деятельность агентств по продаже билетов \nЭта группировка не включает: \n- деятельность студий звукозаписи, см. 92.11 \n- деятельность кинотеатров, см. 92.13', 1997, '92.3', 0);
