INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(2030, '95', 'Деятельность домашних хозяйств с наемными работниками', 'Деятельность домашних хозяйств с наемными работниками \nЭта группировка  строго  ограничивает  виды деятельности домашних хозяйств с  наемными  работниками,  такими  как горничные,   повара,    официанты,   слуги,   дворецкие, прачки,   садовники,   привратники,   конюхи,    шоферы, сторожа,  гувернантки,   приходящие    няни,    домашние учителя,  секретари   и  т.п.   Это   позволяет  наемным домашним  работникам  определять  виды  деятельности  их работодателя  при   учете   и   регистрации,   даже  при условии, что  работодатель  является  физическим  лицом. Продукция, произведенная для  собственного  потребления, рассматривается  как   нерыночная   и    оценивается   в национальных счетах согласно стоимости  наемного  труда. Эти услуги не могут предоставляться компаниями', 2029, 'Подраздел PA', 1);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(2031, '95.0', 'Деятельность домашних хозяйств с наемными работниками', 'Деятельность домашних хозяйств с наемными работниками', 2030, '95', 1);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(2032, '95.00', 'Деятельность домашних хозяйств с наемными работниками', 'Деятельность домашних хозяйств с наемными работниками', 2031, '95.0', 0);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(2033, '96', 'Деятельность частных домашних хозяйств по производству товаров для собственного потребления', 'Деятельность частных домашних хозяйств  по  производству товаров для собственного потребления Примечание: Эта  группировка  строго  ограничивает  виды деятельности  домашних   хозяйств   в   производстве  на уровне   прожиточного  минимума  разнообразных  товаров, произведенных для собственного  потребления,  таких  как продукты  питания,   товары   для  строительства  жилищ, одежда.  Однако   домашние  хозяйства,  занятые  главным образом  нерыночной   производственной    деятельностью, например    сельским     хозяйством,    предпочтительнее классифицировать не в группировке 96, а в  группировках, относящихся к сельскому хозяйству', 2029, 'Подраздел PA', 1);
INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(2034, '96.0', 'Деятельность частных домашних хозяйств по производству товаров для собственного потребления', 'Деятельность частных домашних хозяйств  по  производству товаров для собственного потребления', 2033, '96', 1);
