INSERT INTO `dim_okved` (`id`, `code`, `name`, `additional_info`, `parent_id`, `parent_code`, `node_count`) VALUES(2043, '99.00', 'Деятельность экстерриториальных организаций', 'Деятельность экстерриториальных организаций \r\nЭта группировка включает: \r\n- деятельность  международных  организаций  (Организации Объединенных  Наций  и ее специализированных учреждений, региональных  органов  и  т.п.,  Европейских  сообществ, Европейской  ассоциации свободной торговли,  Организации экономического   сотрудничества   и   развития,   Совета таможенного    сотрудничества,   Организации   стран   \r\n- экспортеров  нефти,  Международного   валютного   фонда, Всемирного банка и т.п.) \r\n- деятельность  дипломатических  и  консульских  миссий, если  они  учитываются  по месту их размещения,  а не по стране, которую они представляют \r\n- деятельность     органов    Содружества    Независимых Государств \r\nЭта группировка не включает: \r\n- управление  и   функционирование   дипломатических   и консульских  миссий,  размещенных  за  границей  или при секретариатах международных организаций, см. 75.21', 2042, '99.0', 0);
